lists.dependencies_limit:
function (head, req) {
    var deps = [];
    while(row = getRow()) {
        deps.push(row);
    }
    var sorted = deps.sort(function(a,b) { return req.query.descending !== "true" ? a.value - b.value : b.value - a.value; });    
    // using list_Limit rather than limit because limit appears to limit the initial view set
    // assuming there's a supported convention but using this for now
    var limit = req.query.list_limit && parseInt(req.query.list_limit);
    send(JSON.stringify({ total_rows: deps.length, rows: limit ? sorted.splice(0, limit) : sorted}));
}

lists.search:
function (head, req) {
    Object.keys = Object.keys
      || function (o) { var a = []
                        for (var i in o) a.push(i)
                        return a }
    var set = {};
    var rows = [];
    while(row = getRow()) {
        set[row.id] = { key: row.id, count: set[row.id] ? set[row.id].count + 1 : 1, value: row.value };
    }
    var keys = Object.keys(set);
    for(var i=0; i<keys.length; i++) {
        rows.push(set[keys[i]]);
    }
    send(JSON.stringify({ rows: rows} ));
}

search.map:
function (doc) {
  var descriptionBlacklist =
    [ "for"
    , "and"
    , "in"
    , "are"
    , "is"
    , "it"
    , "do"
    , "of"
    , "on"
    , "the"
    , "to"
    , "as"
    ]

  if (doc.name) { // There aren't any better attributes for check if isPackage()
    if (doc.name) {
      var names = [doc.name];
      if (doc.name.indexOf('-') !== -1) doc.name.split('-').forEach(function (n) {names.push(n)});
      if (doc.name.indexOf('_') !== -1) doc.name.split('_').forEach(function (n) {names.push(n)});
      names.forEach(function (n) {
        if (n.length > 1) emit(n.toLowerCase(), doc);
      });
    }
    if (doc['dist-tags'] && doc['dist-tags'].latest && (
        doc.versions[doc['dist-tags'].latest].keywords || doc.versions[doc['dist-tags'].latest].tags
        )) {
      var tags = (doc.versions[doc['dist-tags'].latest].keywords || doc.versions[doc['dist-tags'].latest].tags)
      tags.forEach(function (tag) {
        tag.split(' ').forEach(function (t) {
          if (t.length > 0) emit(t.toLowerCase(), doc);
        });
      })
    }
    if (doc.description) {
      doc.description.split(' ').forEach(function (d) {
        d = d.toLowerCase();
        while (d.indexOf('.') !== -1) d = d.replace('.', '');
        while (d.indexOf('\n') !== -1) d = d.replace('\n', '');
        while (d.indexOf('\r') !== -1) d = d.replace('\n', '');
        while (d.indexOf('`') !== -1) d = d.replace('`', '');
        while (d.indexOf('_') !== -1) d = d.replace('_', '');
        while (d.indexOf('"') !== -1) d = d.replace('"', '');
        while (d.indexOf('\'') !== -1) d = d.replace('\'', '');
        while (d.indexOf('(') !== -1) d = d.replace('(', '');
        while (d.indexOf(')') !== -1) d = d.replace(')', '');
        while (d.indexOf('[') !== -1) d = d.replace('[', '');
        while (d.indexOf(']') !== -1) d = d.replace(']', '');
        while (d.indexOf('{') !== -1) d = d.replace('{', '');
        while (d.indexOf('}') !== -1) d = d.replace('}', '');
        while (d.indexOf('*') !== -1) d = d.replace('*', '');
        while (d.indexOf('%') !== -1) d = d.replace('%', '');
        while (d.indexOf('+') !== -1) d = d.replace('+', '');
        if (descriptionBlacklist.indexOf(d) !== -1) d = '';
        if (d.length > 1) emit(d, doc);
      })
    }
  }
}

dependencies.map:
function (doc) {
  if (doc['dist-tags'] && doc['dist-tags'].latest) {
    var dist = doc.versions[doc['dist-tags'].latest];
    for (i in dist.dependencies) {
      emit(i, dist.dependencies[i])
    }
  }
}

updated.map:
function (doc) {
      var l = doc["dist-tags"].latest
        , t = doc.time && doc.time[l]
      if (t) emit(t, 1)
    }

analytics.map:
function (doc) {
             if (doc.time) {
               if (doc.time.modified) {
                 emit(['latest', doc.time.modified], 1);
               }
               if (doc.time.created) {
                 emit(['created', doc.time.created], 1);
               }
               for (i in doc.time) {
                 emit(['update', doc.time[i]], 1);
               }
             }
           }

0.2.5.readme:
cordova-plugin-hipmob-android
---------------------------
The Hipmob Cordova plugin for Android provides Phonegap applications access to helpdesk, live chat and support CRM tools using the Hipmob platform. For more information visit https://www.hipmob.com.

It is compatible with Android 2.1 and higher, and works on versions of Cordova greater than 2.9.0.

Installation
---------------------------
Install the plugin using the Cordova command-line interface:

    cordova plugin add https://github.com/hipmob/cordova-plugin-hipmob-android.git

On Windows, you may have issues with the install (we've observed Git errors when trying to do the fetch). If that's the case, you'll want to clone the repository and then do a local add: ***make sure you clone the plugin to a directory outside your app***.

    git clone https://github.com/hipmob/cordova-plugin-hipmob-android.git <directory location of plugin>
    cd <root of application>
    cordova plugin add <directory location of plugin>

Usage
---------------------------
To use the Hipmob library, you'll need to get a free account at https://www.hipmob.com/. Once you have an account, visit https://manage.hipmob.com/#apps to get your application ID. You can then open a support chat window from a button:

    if( window.plugins && window.plugins.Hipmob ) {
        var hipmob_app_id = '<HIPMOB APPLICATION ID>';
        var Hipmob = window.plugins.Hipmob;

        Hipmob.openChat(hipmob_app_id, {
	'title': 'Help',
	'user': '<hipmob user id>',
	'name': 'Femi',
	'email': 'femi@hipmob.com',
	'context': 'shopping for shoes',
	'location': 'At home'
	});
    } else {
      alert('Hipmob plugin not available/ready.');
    }

To open a helpdesk search window, you can do:

    if( window.plugins && window.plugins.Hipmob ) {
        var hipmob_app_id = '<HIPMOB APPLICATION ID>';
        var Hipmob = window.plugins.Hipmob;

        Hipmob.openHelpdeskSearch(hipmob_app_id, {
	'title': 'Help',
	'query': 'Android',
	'user': '<hipmob user id>',
	'name': 'Femi',
	'email': 'femi@hipmob.com',
	'context': 'shopping for shoes',
	'location': 'At home'
	});
    } else {
      alert('Hipmob plugin not available/ready.');
    }

To open an instant help window, you can do:

    if( window.plugins && window.plugins.Hipmob ) {
        var hipmob_app_id = '<HIPMOB APPLICATION ID>';
	var helpdesk_article_url = '<helpdesk article url>';
        var Hipmob = window.plugins.Hipmob;

        Hipmob.openHelpdeskArticle(hipmob_app_id, helpdesk_article_url, {
	'title': 'Help',
	'user': '<hipmob user id>',
	'name': 'Femi',
	'email': 'femi@hipmob.com',
	'context': 'shopping for shoes',
	'location': 'At home'
	});
    } else {
      alert('Hipmob plugin not available/ready.');
    }

